mapper.select:
SELECT

    CASE WHEN t${index}.issue_id IS NOT NULL THEN ${rule.id} END AS t${index}

FROM
agile_issue ai

    LEFT JOIN (SELECT issue_id FROM agile_issue WHERE issue_id = #{issueId} AND (${rule.sqlQuery})) t${index} ON ai.issue_id = t${index}.issue_id

WHERE
    project_id = #{projectId}
AND ai.issue_id = #{issueId}
